package com.gaoge.view.practise;

import android.content.Context;
import android.preference.PreferenceCategory;
import android.util.AttributeSet;
import android.view.View;

import com.orange.test.textImage.R;

public class OrangePreferenceCategory extends PreferenceCategory {

    public OrangePreferenceCategory(Context context,AttributeSet attrs) {
        super(context,attrs);
    }
    
    @Override
    protected void onBindView(View view) {
        // TODO Auto-generated method stub
        super.onBindView(view);
        view.setBackgroundColor(R.color.settings_background);
    }

}
